import React from 'react';
import ReactDOM from 'react-dom';
import '../css/Menu.css';

class Menu extends React.Component {
    constructor() {
        super();
        this.state = {
            tasks: [
                {id: 0, title: 'task1', status: 'new', priority: 'low', endDate: '01.01.2019', factEndDate: '-'},
                {id: 1, title: 'task2', status: 'inProgress', priority: 'low', endDate: '01.01.2019', factEndDate: '-'},
                {id: 2, title: 'task3', status: 'done', priority: 'low', endDate: '01.01.2019', factEndDate: '-'}
            ]
        };
        this.addTask = this.addTask.bind(this);
    }
    addTask() {
        this.setState(state => {
            let { tasks } = state;
            tasks.push({
                id: tasks.length !== 0 ? tasks.length : 0,
                title: 'task4',
                status: 'new',
                priority: 'low',
                endDate: '01.01.2019', 
                factEndDate: '-'
            });
        });
    }
    render() {
        // const { tasks } = this.state;

        return (
        <div id='menu'>
            <button id='buttonAddTask' onClick={this.addTask}>
                Добавить задачу
            </button>
            <input id='inputSerch' type='text' placeholder='Поиск'></input>
            <label className='menuLabels'>Всего - {this.props.tasks.length}</label>
            {/* <label className='menuLabels'>Новых - {tasks.filter(task => task.status == 'new').length}</label>
            <label className='menuLabels'>В работе - {tasks.filter(task => task.status =='inProgress').length}</label>
            <label className='menuLabels'>Завершено - {tasks.filter(task => task.status == 'done').length}</label> */}
        </div>);

    }
}

export default Menu;
