import React from 'react';
import ReactDOM from 'react-dom';
import '../css/Menu.css';
import PopUp from './PopUp';

class Menu extends React.Component {
    constructor() {
        super();
        this.state = {
            tasks: [
                {id: 0, desc: 'task1', status: 'new', priority: 'low', endDate: '01.01.2019', factEndDate: '-'},
                {id: 1, desc: 'task2', status: 'inProgress', priority: 'low', endDate: '01.01.2019', factEndDate: '-'},
                {id: 2, desc: 'task3', status: 'done', priority: 'low', endDate: '01.01.2019', factEndDate: '-'}
            ],
            popupMenuSeen: false
        };
        this.openPopupMenu = this.openPopupMenu.bind(this);
    }

    openPopupMenu() {
        // let { tasks } = this.state;
        // tasks.push({
        //     id: tasks.length !== 0 ? tasks.length : 0,
        //     title: 'task4',
        //     status: 'inProgress',
        //     priority: 'low',
        //     endDate: '01.01.2019', 
        //     factEndDate: '-'
        // });
        // this.setState(tasks);
        this.setState({popupMenuSeen: !this.state.popupMenuSeen});
    }

    addTask(task) {
        let { tasks } = this.state;
        task['id'] = tasks.length !== 0 ? tasks.length : 0;
        task['factEndDate'] = '-';
        tasks.push(task);
        this.setState(tasks);
    }

    render() {
        const { tasks } = this.state;
        return (
        <div id='menu'>
            <button id='buttonAddTask' onClick={this.openPopupMenu}>Добавить задачу</button>
            {this.state.popupMenuSeen ? <PopUp toggle={this.openPopupMenu} task={null}></PopUp> : null}
            <input id='inputSerch' type='text' placeholder='Поиск'></input>
            <label className='menuLabels'>Всего - {tasks.length}</label>
            <label className='menuLabels'>Новых - {tasks.filter(task => task.status == 'new').length}</label>
            <label className='menuLabels'>В работе - {tasks.filter(task => task.status =='inProgress').length}</label>
            <label className='menuLabels'>Завершено - {tasks.filter(task => task.status == 'done').length}</label>
        </div>);

    }
}

export default Menu;
