import React from "react";
import '../css/PopUp.css';

class PopUp extends React.Component {

    constructor(props) {
        super(props);
        this.state = { task : {
                id: '', 
                desc: '', 
                status: '', 
                priority: '', 
                endDate: '', 
                factEndDate: ''
            }
        };

        this.handleClick = this.handleClick.bind(this);
        this.addTask = this.addTask.bind(this);
    }

    handleClick() {
        this.props.toggle();
    }

    onDescriptionInputChange(event) {
        let {task} = this.state;
        task['desc'] = event.target.value;
    }

    addTask() {

    }

    render() {
        let {task} = this.state;
        return (
            <div id="modal">
                <div id="modalContent">
                    <span id="close" onClick={this.handleClick}>&times;</span>
                    <h1>Создание/редактирование задачи</h1>
                    <div id='firstBlock'>
                        <label>Описание:</label>
                        <input type='text' id='descriptionInput' value={task['desc']} onChange={this.onDescriptionInputChange}/>
                    </div>
                    <div id='secondBlock'>
                        <label>Приоритет:</label>
                        <select id='selectPriority'>
                            <option>Низкий</option>
                            <option>Средний</option>
                            <option>Высокий</option>
                        </select>
                        <label>Статус:</label>
                        <select id='selectStatus'>
                            <option>Новый</option>
                            <option>В работе</option>
                            <option>Завершено</option>
                        </select>
                    </div>
                    <div id='thirdBlock'>
                        <label>Крайний срок:</label>
                        <input type='text' id='endDateInput' />
                    </div>
                    <button id='buttonSaveTask' onClick={this.addTask}>Сохранить</button>
                </div>
            </div>
        );
    }
}

export default PopUp;
