import React from "react";
import DatePicker from 'react-date-picker';
import '../css/PopUp.css';

const statuses = {
    new: 'Новый',
    inProgress: 'В работе',
    done: 'Завершено'
};

const priorities = {
    low: 'Низкий',
    avg: 'Средний',
    high: 'Высокий'
};

class PopUp extends React.Component {

    constructor(props) {
        super(props);
        if (this.props.task == null) {
            this.state = { task : {
                    id: '', 
                    desc: '', 
                    status: statuses['new'], 
                    priority: priorities['low'], 
                    endDate: new Date(), 
                    factEndDate: ''
                },
                editingTask: false,
            };
        }
        else {
            this.state = {task: this.props.task, editingTask: true};
        }

        this.closeClick = this.closeClick.bind(this);
        this.saveTask = this.saveTask.bind(this);
        this.onDescriptionInputChange = this.onDescriptionInputChange.bind(this);
        this.onSelectPriorityChange = this.onSelectPriorityChange.bind(this);
        this.onSelectStatusChange = this.onSelectStatusChange.bind(this);
        this.onDatePickerChange = this.onDatePickerChange.bind(this);
    }

    closeClick() {
        this.props.toggle();
    }

    onDescriptionInputChange(event) {
        let {task} = this.state;
        task['desc'] = event.target.value;
        this.setState(task);
    }

    onSelectPriorityChange(event) {
        let {task} = this.state;
        task.priority = event.target.value;
        this.setState(task);
    }

    onSelectStatusChange(event) {
        let {task} = this.state;
        task.status = event.target.value;
        this.setState(task);
    }

    onDatePickerChange(date) {
        let {task} = this.state;
        task.endDate = new Date(date.getFullYear(), (date.getMonth() + 1), date.getDate()).toString();
        console.log(task.endDate);
        this.setState(task);
    }

    saveTask() {
        const {task} = this.state; 
        const {editingTask} = this.state;
        if (editingTask) {
            this.props.editTask(task);
        } else {
            this.props.addTask(task);
        }
        this.props.toggle();
    }

    render() {
        let {task} = this.state;
        return (
            <div id="modal">
                <div id="modalContent">
                    <span id="close" onClick={this.closeClick}>&times;</span>
                    <h1>Создание/редактирование задачи</h1>
                    <div id='firstBlock'>
                        <label>Описание:</label>
                        <input type='text' id='descriptionInput' value={task.desc} onChange={this.onDescriptionInputChange}/>
                    </div>
                    <div id='secondBlock'>
                        <label>Приоритет:</label>
                        <select id='selectPriority' value={task.priority} onChange={this.onSelectPriorityChange}>
                            <option>{priorities['low']}</option>
                            <option>{priorities['avg']}</option>
                            <option>{priorities['high']}</option>
                        </select>
                        <label>Статус:</label>
                        <select id='selectStatus' value={task.status} onChange={this.onSelectStatusChange} disabled={this.state.editingTask ? false : true}>
                            <option>{statuses['new']}</option>
                            <option>{statuses['inProgress']}</option>
                            <option>{statuses['done']}</option>
                        </select>
                    </div>
                    <div id='thirdBlock'>
                        <label>Крайний срок:</label>
                        {/* <input type='text' id='endDateInput' value={task.endDate} onChange={this.onEndDateInputChange} onClick={this.onEndDateInputClick}/> */}
                        <DatePicker id='datePicker' value={task.endDate} onChange={this.onDatePickerChange}></DatePicker>
                    </div>
                    <button id='buttonSaveTask' onClick={this.saveTask}>Сохранить</button>
                </div>
            </div>
        );
    }
}

export default PopUp;
