import React from "react";
import '../css/PopUp.css';

export default class PopUp extends React.Component {
    handleClick = () => {
      this.props.toggle();
    };

    render() {
      return (
        <div id="modal">
          <div id="modal_content">
            <span id="close" onClick={this.handleClick}>
              &times;
            </span>
            <form>
              <h3>Register!</h3>
              <label>
                Name:
                <input type="text" name="name" />
              </label>
              <br />
              <input type="submit" />
            </form>
          </div>
        </div>
      );
    }
  }
